insert  into `categories`(`id`,`alias`,`name`,`desc`,`logo`) values (1,'pos','POS','<li><img src=\"images/check-icon.png\" alt=\"check-icon\"> Fast order/billing</li>\r\n<li><img src=\"images/check-icon.png\" alt=\"check-icon\"> Smart data structure</li>\r\n<li><img src=\"images/check-icon.png\" alt=\"check-icon\"> Efficient statistic report</li>','pos-icon.png'),(2,'crm','CRM','<li><img src=\"images/check-icon.png\" alt=\"check-icon\"> Simple customer register</li>\r\n<li><img src=\"images/check-icon.png\" alt=\"check-icon\"> Smart searching</li>\r\n<li><img src=\"images/check-icon.png\" alt=\"check-icon\"> Smart email sending</li>','crm-icon.png'),(3,'hrm','HRM','<li><img src=\"images/check-icon.png\" alt=\"check-icon\"> Real-time cost management</li>\r\n<li><img src=\"images/check-icon.png\" alt=\"check-icon\"> Labor/Tax law compliance</li>\r\n<li><img src=\"images/check-icon.png\" alt=\"check-icon\"> Working efficiency traceability </li>','hrm-icon.png'),(4,'seo','SEO','<li><img src=\"images/check-icon.png\" alt=\"check-icon\"> Nalyze keyword to target</li>\r\n<li><img src=\"images/check-icon.png\" alt=\"check-icon\"> Optimize html code</li>\r\n<li><img src=\"images/check-icon.png\" alt=\"check-icon\"> Optimize contents writing</li>','seo-icon.png'),(5,'web-design','WEB DESIGN','<li><img src=\"images/check-icon.png\" alt=\"check-icon\"> Web design, branding professionally and create. Clean coding and friendly.</li>','webdesign-icon.png');

